#include<iostream>#include<deque>using namespace std;int* maxInSlidingWindow(int* a,int n,int w,int* b){	deque<int> Q;	// Q will store the indeces..	int j=0;		int i=0;	for( ;i<w;i++)	{		while(!Q.empty() && a[i] > a[Q.front()])			Q.pop_front();					Q.push_back(i);	}	b[j++] = a[Q.front()];	for( ; i< n ;i++)	{		while(!Q.empty() && i-w >= Q.front())			Q.pop_front();		while(!Q.empty() && a[i] > a[Q.front()])			Q.pop_back();		Q.push_back(i);				b[j++] = a[Q.front()];	}		return b;}int main(){	int n,w;	cout<<"Enter array size:";	cin>>n;	int a[n];	cout<<"Enter elements:";	for(int i=0;i<n;i++)		cin>>a[i];	cout<<"Enter window size:";	cin>>w;		int b[n-w+1];	maxInSlidingWindow(a,n,w,b);	cout<<"Answer:";	for(int i=0;i<=n-w;i++)		cout<<b[i]<<" ";	cout<<endl;return 0;}